import React from 'react'
import './AddEditProblems.css'
import { useNavigate,useState } from 'react-router-dom';
import axios from 'axios'; // Import Axios for HTTP requests
export const AddEditProblems = () => {
    const navigate = useNavigate(); // Hook for navigation

    const[code, setCode] = useState('');
    const [notes, setNotes] = useState('');

    const goBack = () => {
      navigate(-1); // Navigates to the previous page
    };

    const handleGenerateNotes = () => {
        // Implement note generation logic if needed
        console.log('Generate Notes button clicked');
    };

    const handleSubmit = async () => {
        try {
            const response = await axios.post('/api/problems', { code, notes });
            console.log('Saved to MongoDB:', response.data);
            // Optionally, add logic to navigate to a success page or show a success message
        } catch (error) {
            console.error('Error saving to MongoDB:', error);
            // Handle error (e.g., show an error message)
        }
    };

  return (
    <div className="container">
      <h1 className="title">Add/Edit Problems</h1> 
      <div className="code-section">
        <label htmlFor="code">Add Code Here:</label>
        <textarea id="code" placeholder="Type your code here..." />
      </div>
      <div className="notes-section">
        <label htmlFor="question">Your Question:</label>
        <textarea id="question" placeholder="Write your question here..." />
        <button>Generate Notes</button>
      </div>
      <button className="submit-button">Submit</button>
      <button className="go-back-button" onClick={goBack}>Go Back</button>
    </div>
  )
}

export default AddEditProblems
